mapper.select:

SELECT COUNT(*)
  FROM trailUsed
 WHERE trailNo = #{trailNo}


SELECT COUNT(*)
  FROM trailStar
 WHERE trailNo = #{trailNo}


SELECT COUNT(*)
  FROM trailCmt
 WHERE trailNo = #{trailNo}


SELECT COUNT(*)
  FROM trail
 WHERE name = #{name}

mapper.delete:

DELETE coords
 WHERE type = 'trail'
   AND useNo = #{trailNo}

mapper.select:

SELECT tagName
  FROM trailTag
 WHERE trailNo = #{trailNo}


SELECT COUNT(*)
  FROM trailUsed tu, walkLog w
 WHERE tu.walklogno = w.walkLogNo
   AND w.userNo = #{usersVo.userNo}
   AND tu.trailNo = #{trailNo}


SELECT COUNT(*)
  FROM trailStar ts, trail t, users u
 WHERE t.trailNo = ts.trailNo
   AND ts.userNo = u.userNo
   AND ts.userNo = #{usersVo.userNo}
   AND t.trailNo = #{trailNo}

SELECT ort.walkLogNo ,ort.userNo ,ort.name userName ,ort.code ,ort.distance ,ort.logTime ,ort.content ,ort.title ,ort.regDate ,ort.cnt FROM (SELECT ROWNUM rn ,ot.walkLogNo ,ot.userNo ,ot.name ,ot.code ,ot.distance ,ot.logTime ,ot.content ,ot.title ,ot.regDate ,ot.cnt FROM (SELECT w.walklogNo ,u.userNo ,u.name ,u.code ,w.distance ,w.logTime ,w.content ,w.title ,TO_CHAR(w.regdate, 'YY-MM-DD HH24:MI') regDate ,gt.cnt FROM users u, trailUsed tu, walkLog w LEFT OUTER JOIN ( SELECT COUNT(likeNo) cnt ,useNo FROM userLike WHERE type = 'walkLog' GROUP BY useNo ) gt ON w.walkLogNo = gt.useNo WHERE tu.walklogno = w.walkLogNo AND w.userNo = u.userNo AND w.status = 'T' AND w.security = '공개' AND tu.trailNo = #{trailNo}
ORDER BY DECODE(w.userNo, #{userNo}, 1), regDate DESC, w.walklogNo DESC
ORDER BY DECODE(w.userNo, #{userNo}, 1), gt.cnt DESC NULLS LAST, regDate DESC, w.walklogNo DESC
) ot ) ort WHERE ort.rn >= 1 AND ort.rn <= 10

SELECT ort.walkLogNo ,ort.meetingNo ,ort.userNo ,ort.name userName ,ort.code ,ort.distance ,ort.logTime ,ort.content ,ort.title ,ort.regDate FROM (SELECT ROWNUM rn ,ot.walkLogNo ,ot.meetingNo ,ot.userNo ,ot.name ,ot.code ,ot.distance ,ot.logTime ,ot.content ,ot.title ,ot.regDate FROM (SELECT w.walklogNo ,m.meetingNo ,u.userNo ,u.name ,u.code ,w.distance ,w.logTime ,w.content ,w.title ,TO_CHAR(w.regdate, 'YY-MM-DD HH24:MI') regDate FROM users u, meeting m, walkLog w LEFT OUTER JOIN ( SELECT COUNT(likeNo) cnt ,useNo FROM userLike WHERE type = 'walkLog' GROUP BY useNo ) gt ON w.walkLogNo = gt.useNo WHERE m.meetingNo = w.meetingNo AND m.userNo = w.userNo AND w.userNo = u.userNo AND w.status = 'T' AND w.security = '공개' AND m.trailNo = #{trailNo}
ORDER BY regDate DESC, w.walklogNo DESC
ORDER BY gt.cnt DESC NULLS LAST, regDate DESC, w.walklogNo DESC
) ot ) ort WHERE ort.rn >= 1 AND ort.rn <= 10


 	SELECT COUNT(*)
	  FROM meetingInfo
	 WHERE meetingNo = #{meetingNo}

